import React from 'react';

import Footer from './components/Footer.js'
import AboutUS from './components/AboutUs.js'
import main_img from './components/gym_buddy_main_img.png'; // Import the image
import logo from './components/logo.png'; // Import the image
import './App.css';
import Stats from './components/Stats.js'
import NavBar from './components/NavBar';
import Testimonials from './components/Testimonials.js'
import LandingPage from './components/LandingPage.js';
import HowItWorks from './components/HowItWorks.js';




function App() {

  const isMobile = window.innerWidth <= 768;
  const landingPageRef = useRef(React.createRef());

  const scrollHandler = (elmRef) => {
    if (elmRef.current) {
      window.scrollTo({ top: elmRef.current.offsetTop, behavior: 'smooth' });
    }
  };

  const openDownloadLink = () => {
    // Open the link in a new tab
    const downloadWindow = window.open(
      'https://apps.apple.com/us/app/gymbuddy-connect-workout/id6446038725',
      '_blank'
    );

    if (downloadWindow) {
      downloadWindow.focus();
    }
  };
    const home = useRef(LandingPage);
    const features = useRef();
    const aboutus = useRef();
    const contact = useRef();
    const download = useRef();
  return (
    <div className="App">






<div className="navbar-container">
    <div className="logo-container">
    <img
            className="logo"
            src={logo} alt="icon"
            onClick={() => scrollHandler(landingPageRef)}
          />
    </div>

    <div className="button-container">
      {isMobile ? (
        <div className="down-button" onClick={openDownloadLink}>Download ✨</div>
      ) : (
        <>
          <div className="nav-button">Features</div>
          <div className="nav-button">About Us</div>
          <div className="nav-button">Contact</div>
          <div className="down-button" onClick={openDownloadLink}>Download ✨</div>
        </>
      )}
    </div>
  </div>
      <LandingPage /> 







         <NavBar/>
          <LandingPage/>
          <Stats></Stats>
          <HowItWorks/>


      <header className="App-header">

      </header>
      <Testimonials/>
      <AboutUS/>
      <Footer />
     </div>


  );
} 

export default App;
