import React from 'react';
import './NavBar.css';
import LandingPage from './LandingPage.js';
import logo from './img/logo2.png';
import {useRef} from 'react';
//import all the pages

import feature from './HowItWorks.js';
import contact from './Footer.js'
import about from './AboutUs.js'
function NavBar() {
  const isMobile = window.innerWidth <= 768;
    const landingPageRef = useRef(React.createRef());
  
    const scrollHandler = (elmRef) => {
      if (elmRef.current) {
        window.scrollTo({ top: elmRef.current.offsetTop, behavior: 'smooth' });
      }
    };
  
    const openDownloadLink = () => {
      // Open the link in a new tab
      const downloadWindow = window.open(
        'https://apps.apple.com/us/app/gymbuddy-connect-workout/id6446038725',
        '_blank'
      );
  
      if (downloadWindow) {
        downloadWindow.focus();
      }
    };
  const home = useRef(LandingPage);
  const features = useRef(feature);
  const aboutus = useRef(about);
  const contact = useRef(contact);
  const download = useRef();
//here add all the respecting js files for each page

  return (
      <div className="navbar-container">
      <div className="logo-container">
      <img
              className="logo"
              src={logo} alt="icon"
              onClick={() => scrollHandler(landingPageRef)}
            />
      </div>

      <div className="button-container">
        {isMobile ? (
          <div className="down-button" onClick={openDownloadLink}>Download ✨</div>
        ) : (
          <>
            <div className="nav-button">Features</div>
            <div className="nav-button">About Us</div>
            <div className="nav-button">Contact</div>
            <div className="down-button" onClick={openDownloadLink}>Download ✨</div>
          </>
        )}
      </div>
    </div>
  );
};

export default NavBar;
